import React, {Component} from 'react';
import '../App.css';

class QuestView extends Component {
    constructor(props){
        super(props);
        this.fullHeight = this.fullHeight.bind(this);
    }
    fullHeight(){
        //var body_height = parseInt(document.body.scrollHeight) - (2*parseInt(getComputedStyle(document.getElementsByID("questView"), null).borderWidth));
        //alert(body_height);
        //document.getElementsByID("questView").style.height = body_height + "px";
        alert("hello");
        alert(document.body.scrollHeight);
        alert(getComputedStyle(document.getElementById("questView"), null).borderWidth);

    };
    render(){
        return (
            <div class="standardView">
                <div id="questView">hello</div>
                {this.fullHeight()}
            </div>
        );
    }
}

export default QuestView;
