import React, {Component} from 'react';
import '../App.css';
import Button from "../Button";

//files
import btn_claim from "../img/UI/buttons/btn_claim.png"
import coin from "../img/UI/difficulties/small_coin.png"

class QuestCard extends Component {
    render() {
        return (
            <div className="questCard">
                <div className="questCardLeft">
                    {/*read title and description from props*/}
                    {/*temporary for visual reference*/}
                    <div className="questCardTitle">{this.props.quest_name}</div>
                    {/*difficulty*/}
                    {this.questDifficulty(this.props.quest_diff)}
                    <div className="questCardDetails">{this.props.quest_details}</div>

                </div>
                <div className="questCardRight"><Button init_height={this.props.initial_height} btn_src={btn_claim} btn_id="btn_claim" btn_action="card_claim" btn_size="0.055"/></div>
            </div>
        );
    }

    questDifficulty(diff){
        var diff_coins;
        switch(diff){
            case "diff_trivial":
                diff_coins = 1;
                break;
            case "diff_easy":
                diff_coins = 2;
                break;
            case "diff_medium":
                diff_coins = 3;
                break;
            case "diff_hard":
                diff_coins = 4;
                break;
            default:
                console.log("Error: difficulty not recorded");
        }
        var i;
        var coins_arr = [];
        for (i=0; i < diff_coins; i++){
            coins_arr.push(<img src={coin}/>)
        }
        // return {coins_arr};
        // if(diff === "diff_trivial"){
        //     diff_coins = 1;
        // }
        // else if(diff === "diff_easy"){
        //
        // }
        // else if(diff === "diff_medium"){
        //
        // }
        // else if(diff === "diff_hard"){
        //
        // }
        // else{
        //
        // }

    }
}

export default QuestCard;
