import React, {Component} from 'react';
import '../App.css';

class QuestView extends Component {
    fullHeight(){
        var body_height = parseInt(document.body.scrollHeight) - (2*parseInt(getComputedStyle(document.body, null).borderWidth));
        alert(body_height);
        document.getElementsByID("questView").style.height = body_height + "px";
    };
    render(){
        this.fullHeight();
        return (
            <div id={"questView"}>hello</div>
        )
    }
}

export default QuestView;
