import React, {Component} from 'react';
import ReactDOM from 'react-dom'
import '../App.css';

class QuestView extends Component {
    constructor(props){
        super(props);
        this.fullHeight = this.fullHeight.bind(this);
    }
    fullHeight(){
        var body_height = parseInt(document.body.scrollHeight) - 60;
        //(2*parseInt(getComputedStyle(document.getElementsByID("questView"), null).borderWidth));
        alert(body_height);
        let div_element = document.getElementById("questView");
        ReactDOM.findDOMNode(div_element).style.borderWidth = body_height + "px";
        //this.document.getElementById("questView").style.height = body_height + "px";
        // alert("hello");
        // alert(document.body.scrollHeight);
        //alert(document.getElementById("questView").getPropertyValue("border-width"));


    };
    render(){
        return (
            <div className="standardView">
                <div id="questView">hello</div>
            </div>
        ),
        fullHeight();
    }
}

export default QuestView;
